$(function () {
	$('.pages .inside li a').each(function (index) {
		if (this.href.trim() == window.location) $(this).addClass("active")
	});
	$(".follow_info li strong a").text(function(index, text) {
    	return text.substr(0, 36);
    });
	$("#copyright").after("<li><a href='https://elletricity.com/resources'>Twittlr</a> Theme by Gabrielle Wee.</li><li>This is not Twitter or any site associated with Twitter.</li>");
	$('#notes > li:first-child:contains(notes)').html(function() {
	    return $(this).html().replace('notes', '<span>notes</span>');
	});
	$('#notes > li:first-child:contains(1 note)').html(function() {
	    return $(this).html().replace('1 note', '1 <span>note</span>');
	});
	$("#ask_form").parent().addClass("before").before(twittlr.ask);
	$("#submit_form").parent().addClass("before").before(twittlr.submit);
	$('#posts > h1 span:contains(Search results)').html(function() {
	    return $(this).html().replace('Search r', 'R');
	});
});

