$(window).load(function () {
	$("#posts").infinitescroll({
		navSelector: '#pag:last',
		nextSelector: '#next:last',
		itemSelector: '.appended',
		contentSelector: '#post_grid',
		loading: {
			finishedMsg: "You've reached the end of the Internet.",
			img: "data:image/gif;base64,",
			msg: null,
			msgText: "",
			selector: null,
			speed: 'fast',
			start: undefined
		}
	}, function (arrayOfNewElems) {
		$(".zoom_frame", this).colorbox({
			photo: true,
			maxHeight: "85%",
			maxWidth: "85%",
			fixed: true
		});
		$('.video_box', this).each(function () {
			var videoPostID = $(this).attr("id");
			var $videoPost = $(this);
			$.ajax({
				url: 'https://' + twittlr.name + '.tumblr.com/api/read/json?id=' + videoPostID,
				dataType: 'jsonp',
				timeout: 50000,
				success: function (data) {
					var player = data.posts[0]['video-player-500'];
					$videoPost.html(player).fluidTumblrVids()
				}
			})
		});
		$('.ps-highres', this).imagesLoaded(function () {
			$(".ps-highres").removeClass("invisible");
			$(".ps-highres").tumblrPhotoset({
				width: "100%",
				highres: true,
				margin: 5,
				onInit: function () {},
				onComplete: function () {
					a = true;
					$(".ps-highres a").colorbox({
						photo: true,
						maxHeight: "85%",
						maxWidth: "85%",
						fixed: true,
						title: function () {
							var newCaption = $('.ps-highres img').attr('alt');
							return newCaption
						}
					});
					setTimeout(function () {
						$(".ps-highres").resize(function () {
							$(this).data("tumblrPhotoset").resizePhotoset()
						})
					}, 400)
				}
			})
		});
	});
});